import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import WeaponList from "./components/WeaponList";
import EquipmentList from "./components/EquipmentList";
import Ammo from "./components/Ammo";
import Personnel from "./components/Personnel";
import SpecialPersonnel from "./components/SpecialPersonnel";
import TechnicalEquipments from "./components/TechnicalEquipments";
import UAV from "./components/UAV";
import Vehicle from "./components/Vehicle";
import { Card } from "@mui/material";

function App() {
  const [totalPowerMultiplier, setTotalPowerMultiplier] = useState(0);
  const [totalKilograms, setTotalKilograms] = useState(0);

  // Alt bileşenlerden gelen değerlere göre toplam güç çarpanı ve kilogramları hesapla
  useEffect(() => {
    // Her bir alt bileşenden güç çarpanını ve kilogramı al
    // Bu değerleri toplam güç çarpanı ve kilograma ekleyerek güncelle
    // Örnek olarak, her bir alt bileşenden gelen bir fonksiyon olmalı
    // Bu fonksiyonlar alt bileşenin içinde tanımlanmalı ve gerekirse prop olarak geçirilmelidir
    // Bu örnekte sadece varsayılan değerler kullanılmıştır
    const weaponPowerMultiplier = 1; // Varsayılan değer
    const weaponKilograms = 10; // Varsayılan değer

    const equipmentPowerMultiplier = 2; // Varsayılan değer
    const equipmentKilograms = 5; // Varsayılan değer

    const ammoPowerMultiplier = 0.5; // Varsayılan değer
    const ammoKilograms = 2; // Varsayılan değer

    const personnelPowerMultiplier = 1.5; // Varsayılan değer
    const personnelKilograms = 70; // Varsayılan değer

    // Toplam güç çarpanını ve kilogramları hesapla
    const totalPowerMultiplier =
      weaponPowerMultiplier +
      equipmentPowerMultiplier +
      ammoPowerMultiplier +
      personnelPowerMultiplier;

    const totalKilograms =
      weaponKilograms + equipmentKilograms + ammoKilograms + personnelKilograms;

    // Hesaplanan değerleri state'e kaydet
    setTotalPowerMultiplier(totalPowerMultiplier);
    setTotalKilograms(totalKilograms);
  }, []); // Bu etkileşim sadece bir kez çalışır

  return (
    <div>
      <Header />
      <Card
        sx={{
          marginBottom: "20px",
          padding: "10px",
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gap: "10px",
        }}
      >
        <WeaponList />
        <EquipmentList />
      </Card>

      <Card
        sx={{
          marginBottom: "20px",
          padding: "10px",
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gap: "10px",
        }}
      >
        <TechnicalEquipments />
        <SpecialPersonnel />
      </Card>

      <Card
        sx={{
          marginBottom: "20px",
          padding: "10px",
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gap: "10px",
        }}
      >
        <Vehicle />
        <Ammo />
      </Card>

      <Card
        sx={{
          marginBottom: "20px",
          padding: "10px",
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gap: "10px",
        }}
      >
        <UAV />
        <Personnel />
      </Card>

      {/* Toplam Güç Çarpanı ve Toplam Kilogramı Göster */}
      <Card
        sx={{
          marginBottom: "20px",
          padding: "10px",
          textAlign: "center",
        }}
      >
        <div>
          <strong>Toplam Güç Çarpanı:</strong> {totalPowerMultiplier.toFixed(2)}
        </div>
        <div>
          <strong>Toplam Kilogram:</strong> {totalKilograms.toFixed(2)}
        </div>
      </Card>

      <Footer />
    </div>
  );
}

export default App;
