import React, { useState } from "react";
import {
  Button,
  TextField,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

const MeyveUgulamasi = () => {
  const [meyveler, setMeyveler] = useState([
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Pear", adet: 0, fiyat: 0 },
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Apple", adet: 0, fiyat: 0 },

    // Diğer meyveleri ekleyebilirsiniz
  ]);

  const [toplamMaliyet, setToplamMaliyet] = useState(0);

  const meyveBilgisiGuncelle = (index, adet, fiyat) => {
    const yeniMeyveler = [...meyveler];
    yeniMeyveler[index].adet = adet;
    yeniMeyveler[index].fiyat = fiyat;
    setMeyveler(yeniMeyveler);
  };

  const hesaplaToplamMaliyet = () => {
    const yeniToplamMaliyet = meyveler.reduce((toplam, meyve) => {
      return toplam + meyve.adet * meyve.fiyat;
    }, 0);
    setToplamMaliyet(yeniToplamMaliyet);
  };

  const meyveSil = (index) => {
    const yeniMeyveler = [...meyveler];
    yeniMeyveler.splice(index, 1);
    setMeyveler(yeniMeyveler);
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Fruit Application
      </Typography>

      {/* Meyve Girişi */}
      <div>
        <Typography variant="h6" gutterBottom>
          Fruit Input
        </Typography>
        {meyveler.map((meyve, index) => (
          <div key={index}>
            <TextField
              label={meyve.ad}
              type="number"
              value={meyve.adet}
              onChange={(e) =>
                meyveBilgisiGuncelle(index, e.target.value, meyve.fiyat)
              }
            />
            <TextField
              label="Price"
              type="number"
              value={meyve.fiyat}
              onChange={(e) =>
                meyveBilgisiGuncelle(index, meyve.adet, e.target.value)
              }
            />
          </div>
        ))}
        <Button variant="contained" onClick={hesaplaToplamMaliyet}>
          Calculate Total Cost
        </Button>
      </div>

      {/* Meyve Listesi */}
      <div>
        <Typography variant="h6" gutterBottom>
          Fruit List
        </Typography>
        <List>
          {meyveler.map((meyve, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={`${meyve.ad}: ${meyve.adet} adet x ${meyve.fiyat} TL`}
              />
              <ListItemSecondaryAction>
                <IconButton
                  edge="end"
                  aria-label="delete"
                  onClick={() => meyveSil(index)}
                >
                  <DeleteIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
          ))}
        </List>
      </div>

      {/* Toplam Hesap */}
      <div>
        <Typography variant="h6" gutterBottom>
          Total Cost
        </Typography>
        <Typography>Total Cost: {toplamMaliyet} TL</Typography>
      </div>
    </div>
  );
};

export default MeyveUgulamasi;
