import React, { useState } from "react";
import {
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Paper,
  makeStyles,
} from "@mui/material";

// Stil tanımlamak için makeStyles kullanımı
const useStyles = makeStyles((theme) => ({
  listItem: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: theme.spacing(1),
  },
}));

// Her bir silah için ayrı giriş alanlarına sahip olan component
const WeaponItem = ({
  name,
  quantity,
  points,
  weight,
  onQuantityChange,
  onPointsChange,
  onWeightChange,
}) => {
  const classes = useStyles();

  return (
    <ListItem className={classes.listItem}>
      <ListItemText primary={name} />
      <TextField
        label="Adet"
        type="number"
        step="1"
        value={quantity}
        onChange={(e) => onQuantityChange(e.target.value)}
      />
      <TextField
        label="Puan"
        type="number"
        step="0.1"
        value={points}
        onChange={(e) => onPointsChange(e.target.value)}
      />
      <TextField
        label="Kilogram"
        type="number"
        step="0.1"
        value={weight}
        onChange={(e) => onWeightChange(e.target.value)}
      />
    </ListItem>
  );
};

// Ana WeaponList component'ı
const WeaponList = () => {
  const [weapons, setWeapons] = useState([
    { name: "Pistol", quantity: "", points: "", weight: "" },
    { name: "Rifle", quantity: "", points: "", weight: "" },
    { name: "Shotgun", quantity: "", points: "", weight: "" },
    // Diğer silahları ekleyebilirsiniz
  ]);

  const [totalPower, setTotalPower] = useState(0);
  const [totalWeight, setTotalWeight] = useState(0);

  const calculateTotalPower = () => {
    let calculatedTotalPower = 0;
    let calculatedTotalWeight = 0;

    weapons.forEach((weapon) => {
      const quantity = parseFloat(weapon.quantity) || 0;
      const points = parseFloat(weapon.points) || 0;
      const weight = parseFloat(weapon.weight) || 0;

      calculatedTotalPower += quantity * points;
      calculatedTotalWeight += quantity * weight;
    });

    setTotalWeight(calculatedTotalWeight);

    return calculatedTotalPower;
  };

  const handleQuantityChange = (index, value) => {
    const newWeapons = [...weapons];
    newWeapons[index].quantity = value;
    setWeapons(newWeapons);
  };

  const handlePointsChange = (index, value) => {
    const newWeapons = [...weapons];
    newWeapons[index].points = value;
    setWeapons(newWeapons);
  };

  const handleWeightChange = (index, value) => {
    const newWeapons = [...weapons];
    newWeapons[index].weight = value;
    setWeapons(newWeapons);
  };

  const handleCalculateClick = () => {
    const calculatedTotalPower = calculateTotalPower();
    setTotalPower(calculatedTotalPower);
  };

  return (
    <div>
      <List>
        {weapons.map((weapon, index) => (
          <WeaponItem
            key={index}
            name={weapon.name}
            quantity={weapon.quantity}
            points={weapon.points}
            weight={weapon.weight}
            onQuantityChange={(value) => handleQuantityChange(index, value)}
            onPointsChange={(value) => handlePointsChange(index, value)}
            onWeightChange={(value) => handleWeightChange(index, value)}
          />
        ))}
      </List>
      <Button
        variant="contained"
        color="primary"
        onClick={handleCalculateClick}
      >
        Hesapla
      </Button>
      <Paper elevation={3} style={{ padding: "10px", marginTop: "20px" }}>
        <strong>Toplam Güç Çarpanı:</strong> {totalPower}
        <br />
        <strong>Toplam Kilogram:</strong> {totalWeight}
      </Paper>
    </div>
  );
};

export default WeaponList;
