import React, { useState } from "react";
import { Button, TextField, Typography, Grid, Paper, Box } from "@mui/material";

const FruitEntry = ({ fruit, onUpdate }) => {
  const handleUpdate = (field, value) => {
    onUpdate((prev) => ({ ...prev, [field]: Math.max(0, value) }));
  };

  return (
    <div style={{ display: "flex", alignItems: "center", marginBottom: 16 }}>
      <Typography variant="subtitle1" gutterBottom style={{ minWidth: 120 }}>
        {`${fruit.ad}: `}
      </Typography>
      <TextField
        type="number"
        value={fruit.adet}
        onChange={(e) => handleUpdate("adet", e.target.value)}
        style={{ marginRight: 16 }}
      />
      <Typography variant="subtitle1" gutterBottom style={{ minWidth: 80 }}>
        {`Price: `}
      </Typography>
      <TextField
        type="number"
        value={fruit.fiyat}
        onChange={(e) => handleUpdate("fiyat", e.target.value)}
      />
    </div>
  );
};

const FruitApp = () => {
  const [meyveler, setMeyveler] = useState([
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Pear", adet: 0, fiyat: 0 },
    { ad: "Banana", adet: 0, fiyat: 0 },
    // Diğer meyveleri ekleyebilirsiniz
  ]);

  const [toplamMaliyet, setToplamMaliyet] = useState(0);

  const handleFruitUpdate = (index, updatedFruit) => {
    const newFruits = [...meyveler];
    newFruits[index] = updatedFruit;
    setMeyveler(newFruits);
  };

  const hesaplaToplamMaliyet = () => {
    const yeniToplamMaliyet = meyveler.reduce(
      (toplam, meyve) => toplam + meyve.adet * meyve.fiyat,
      0
    );
    setToplamMaliyet(yeniToplamMaliyet);
  };

  return (
    <Grid container justifyContent="center">
      <Grid item xs={12} md={6} lg={8}>
        <Paper elevation={3} style={{ padding: 16, margin: 16 }}>
          <Typography variant="h5" gutterBottom>
            Meyve Girişi
          </Typography>

          {/* Meyve Girişi */}
          <Box>
            {meyveler.map((meyve, index) => (
              <FruitEntry
                key={index}
                fruit={meyve}
                onUpdate={(updatedFruit) =>
                  handleFruitUpdate(index, updatedFruit)
                }
              />
            ))}
            <Button variant="contained" onClick={hesaplaToplamMaliyet}>
              Toplam Maliyeti Hesapla
            </Button>
          </Box>

          {/* Toplam Hesap */}
          <div style={{ marginTop: 16 }}>
            <Typography>Toplam Maliyet: {toplamMaliyet}</Typography>
          </div>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default FruitApp;
