import React, { useState } from "react";
import { Button, TextField, Typography, Grid, Paper, Box } from "@mui/material";

const WeaponEntry = ({ weapon, onUpdate }) => {
  const handleUpdate = (field, value) => {
    onUpdate((prev) => ({ ...prev, [field]: Math.max(0, value) }));
  };

  return (
    <div style={{ display: "flex", alignItems: "center", marginBottom: 16 }}>
      <Typography variant="subtitle1" style={{ minWidth: 120 }}>
        {`${weapon.name}: `}
      </Typography>
      <TextField
        type="number"
        value={weapon.quantity}
        onChange={(e) => handleUpdate("quantity", e.target.value)}
        style={{ marginRight: 16 }}
        inputProps={{ inputMode: "numeric", pattern: "[0-9]*" }}
      />
      <Typography variant="subtitle1" style={{ minWidth: 80 }}>
        {`Price: `}
      </Typography>
      <TextField
        type="number"
        value={weapon.price}
        onChange={(e) => handleUpdate("price", e.target.value)}
        inputProps={{ inputMode: "numeric", pattern: "[0-9]*" }}
      />
    </div>
  );
};

const Weapons = () => {
  const initialWeaponsState = [
    { name: "Pistol", quantity: 0, price: 0 },
    { name: "Rifle", quantity: 0, price: 0 },
    { name: "Shotgun", quantity: 0, price: 0 },
    // Diğer silahları ekleyebilirsiniz
  ];

  const [weapons, setWeapons] = useState(initialWeaponsState);
  const [totalCost, setTotalCost] = useState(0);

  const handleWeaponUpdate = (index, updatedWeapon) => {
    const newWeapons = [...weapons];
    newWeapons[index] = updatedWeapon;
    setWeapons(newWeapons);
  };

  const calculateTotalCost = () => {
    const newTotalCost = weapons.reduce(
      (total, weapon) => total + weapon.quantity * weapon.price,
      0
    );
    setTotalCost(newTotalCost);
  };

  return (
    <Grid container justifyContent="center">
      <Grid item xs={12} md={6} lg={8}>
        <Paper elevation={3} style={{ padding: 16, margin: 16 }}>
          <Typography variant="h5" gutterBottom>
            Silah Girişi
          </Typography>

          {/* Silah Girişi */}
          <Box>
            {weapons.map((weapon, index) => (
              <WeaponEntry
                key={index}
                weapon={weapon}
                onUpdate={(updatedWeapon) =>
                  handleWeaponUpdate(index, updatedWeapon)
                }
              />
            ))}
            <Button variant="contained" onClick={calculateTotalCost}>
              Toplam Maliyeti Hesapla
            </Button>
          </Box>

          {/* Toplam Hesap */}
          <div style={{ marginTop: 16 }}>
            <Typography>Toplam Maliyet: {totalCost}</Typography>
          </div>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default Weapons;
