import React, { useState } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import WeaponList from "./components/WeaponList";
import EquipmentList from "./components/EquipmentList";
import Ammo from "./components/Ammo";
import Personnel from "./components/Personnel";
import SpecialPersonnel from "./components/SpecialPersonnel";
import TechnicalEquipments from "./components/TechnicalEquipments";
import UAV from "./components/UAV";
import Vehicle from "./components/Vehicle";
import { Card, Typography } from "@mui/material";

function App() {
  // Toplam güç çarpanı ve kilogram state'leri
  const [totalPower, setTotalPower] = useState(0);
  const [totalWeight, setTotalWeight] = useState(0);

  // Her bileşenin kendi hesaplamasını yapıp state'i güncelleyen fonksiyonlar
  const updateTotalPower = (value) => setTotalPower((prev) => prev + value);
  const updateTotalWeight = (value) => setTotalWeight((prev) => prev + value);

  return (
    <div>
      <Header />

      {/* Her bileşenin kendi hesaplamasını yaptığı Card bileşenleri */}
      <WeaponList
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <EquipmentList
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <Card sx={{ marginBottom: "20px", padding: "10px" }}>
        <Typography variant="h6">Toplam Güç Çarpanı: {totalPower}</Typography>
        <Typography variant="h6">Toplam Kilogram: {totalWeight} kg</Typography>
      </Card>
      <TechnicalEquipments
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <SpecialPersonnel
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <Card sx={{ marginBottom: "20px", padding: "10px" }}>
        <Typography variant="h6">Toplam Güç Çarpanı: {totalPower}</Typography>
        <Typography variant="h6">Toplam Kilogram: {totalWeight} kg</Typography>
      </Card>
      <Vehicle
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <Ammo
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <Card sx={{ marginBottom: "20px", padding: "10px" }}>
        <Typography variant="h6">Toplam Güç Çarpanı: {totalPower}</Typography>
        <Typography variant="h6">Toplam Kilogram: {totalWeight} kg</Typography>
      </Card>
      <UAV
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <Personnel
        updateTotalPower={updateTotalPower}
        updateTotalWeight={updateTotalWeight}
      />
      <Card sx={{ marginBottom: "20px", padding: "10px" }}>
        <Typography variant="h6">Toplam Güç Çarpanı: {totalPower}</Typography>
        <Typography variant="h6">Toplam Kilogram: {totalWeight} kg</Typography>
      </Card>

      <Footer />
    </div>
  );
}

export default App;
