import React, { useState } from "react";
import {
  Button,
  TextField,
  Typography,
  Grid,
  Paper,
  IconButton,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

const MeyveUygulamasi = () => {
  const [meyveler, setMeyveler] = useState([
    { ad: "Apple", adet: 0, fiyat: 0 },
    { ad: "Pear", adet: 0, fiyat: 0 },
    { ad: "Banana", adet: 0, fiyat: 0 },
    // Diğer meyveleri ekleyebilirsiniz
  ]);

  const [toplamMaliyet, setToplamMaliyet] = useState(0);

  const meyveBilgisiGuncelle = (index, adet, fiyat) => {
    const yeniMeyveler = [...meyveler];
    yeniMeyveler[index].adet = adet;
    yeniMeyveler[index].fiyat = fiyat;
    setMeyveler(yeniMeyveler);
  };

  const hesaplaToplamMaliyet = () => {
    const yeniToplamMaliyet = meyveler.reduce(
      (toplam, meyve) => toplam + meyve.adet * meyve.fiyat,
      0
    );
    setToplamMaliyet(yeniToplamMaliyet);
  };

  const meyveSil = (index) => {
    const yeniMeyveler = [...meyveler];
    yeniMeyveler.splice(index, 1);
    setMeyveler(yeniMeyveler);
  };

  return (
    <Grid container justifyContent="center">
      <Grid item xs={12} md={6} lg={4}>
        <Paper elevation={3} style={{ padding: 16, margin: 16 }}>
          <Typography variant="h5" gutterBottom>
            Fruit Application
          </Typography>

          {/* Meyve Girişi */}
          <div>
            {meyveler.map((meyve, index) => (
              <div key={index} style={{ marginBottom: 16 }}>
                <TextField
                  label={`${meyve.ad} Quantity`}
                  type="number"
                  value={meyve.adet}
                  onChange={(e) =>
                    meyveBilgisiGuncelle(index, e.target.value, meyve.fiyat)
                  }
                  fullWidth
                />
                <TextField
                  label={`${meyve.ad} Price`}
                  type="number"
                  value={meyve.fiyat}
                  onChange={(e) =>
                    meyveBilgisiGuncelle(index, meyve.adet, e.target.value)
                  }
                  fullWidth
                />
                <IconButton
                  edge="end"
                  aria-label="delete"
                  onClick={() => meyveSil(index)}
                  style={{ marginTop: 8 }}
                >
                  <DeleteIcon />
                </IconButton>
              </div>
            ))}
            <Button variant="contained" onClick={hesaplaToplamMaliyet}>
              Calculate Total Cost
            </Button>
          </div>

          {/* Toplam Hesap */}
          <div style={{ marginTop: 16 }}>
            <Typography variant="h6" gutterBottom>
              Total Cost
            </Typography>
            <Typography>Total Cost: {toplamMaliyet} TL</Typography>
          </div>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default MeyveUygulamasi;
