import React, { useState, useRef } from "react";
import WeaponList from "./components/WeaponList";

const App = () => {
  const weaponListRefs = useRef([]);
  const [totalPower, setTotalPower] = useState(0);
  const [totalWeight, setTotalWeight] = useState(0);

  const handleCalculateAll = () => {
    let calculatedTotalPower = 0;
    let calculatedTotalWeight = 0;

    weaponListRefs.current.forEach((ref) => {
      // Her WeaponList bileşenini referans alarak güç çarpanları ve kilogramları topla
      const { totalPower, totalWeight } = ref.current.calculateTotalPower();
      calculatedTotalPower += totalPower;
      calculatedTotalWeight += totalWeight;
    });

    setTotalPower(calculatedTotalPower);
    setTotalWeight(calculatedTotalWeight);
  };

  return (
    <div>
      {/* WeaponList bileşenlerini ref ile oluşturup listeye ekleyin */}
      <WeaponList
        ref={(ref) => weaponListRefs.current.push(ref)}
        title="Silah Grubu 1"
      />
      <WeaponList
        ref={(ref) => weaponListRefs.current.push(ref)}
        title="Silah Grubu 2"
      />
      <WeaponList
        ref={(ref) => weaponListRefs.current.push(ref)}
        title="Silah Grubu 3"
      />

      {/* Hesapla butonu */}
      <button onClick={handleCalculateAll}>Hesapla Tümü</button>

      {/* Toplam güç çarpanları ve kilogramları gösteren kısım */}
      <div
        style={{
          marginTop: "20px",
          borderTop: "2px solid #ccc",
          paddingTop: "10px",
        }}
      >
        <strong>Toplam Güç Çarpanı:</strong> {totalPower}
        <br />
        <strong>Toplam Kilogram:</strong> {totalWeight}
      </div>
    </div>
  );
};

export default App;
